  grid-row: 1 / 3;
  grid-column: 3 / 3;
  border-left: 1px solid ${({ theme }) => theme.colors.darkPurple};
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  padding: 50px;
  overflow-y: scroll;
`;

const Section = () => {
  const { getNews } = useContext(CryptoApiContext);
  const [articles, setArticles] = useState([]);

  useEffect(async () => {
    // await setArticles(getNews());
    console.log(articles.length);
  }, []);

  // return <Wrapper>{articles.length > 1 ? <div>artykuly</div> : <div> chuja</div>}</Wrapper>;
};

export default Section;
