import styled from "styled-components";

export const Title = styled.h1`
  font-size: ${({ isSmall }) =>
    isSmall ? `${({ theme }) => theme.fontSize.xl}` : `${({ theme }) => theme.fontSize.xl}`};
  color: ${({ theme }) => theme.colors.darkGrey};
`;
