import { Loading } from "components/atoms/Loading/Loading";
import { Title } from "components/atoms/Title/Title";
import { ViewWrapper } from "components/atoms/ViewWrapper/ViewWrapper";
import CoinHandler from "components/molecules/CoinHandler/CoinHandler";
import { CryptoApiContext } from "providers/CryptoApiProvider";
import React, { useContext } from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow-y: ;
`;

const ContentWrapper = styled.div`
  width: 100%;
  height: 90%;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  flex-direction: column;
`;

const Dashboard = () => {
  const { coinsMarket } = useContext(CryptoApiContext);

  return (
    <Wrapper>
      <ViewWrapper>
        <Title> Dashboard </Title>
        <ContentWrapper>
          {coinsMarket.length > 1 ? (
            coinsMarket.map((coin) => <CoinHandler key={coin.id} coin={coin} />)
          ) : (
            <Loading />
          )}
        </ContentWrapper>
      </ViewWrapper>
    </Wrapper>
  );
};

export default Dashboard;
