import styled from "styled-components";

export const ViewWrapper = styled.div`
  margin: 25px;
  background-color: ${({ theme }) => theme.colors.white};
  width: 95%;
  height: ${({ isArticle }) => (isArticle ? fontSize.l : fontSize.xl)};
`;
