import styled from "styled-components";

export const Title = styled.h1`
  font-size: ${({ isSmall }) => (isSmall ? `${({ theme }) => theme.fontSize.s}` : "38px")};
  color: ${({ theme }) => theme.colors.darkGrey};
`;
