import axios from "axios";
import React, { useState } from "react";

const CryptoApiContext = React.createContext({});

const CryptoApiProvider = ({ children }) => {
  const [articles, setArticles] = useState([]);

  const options = {
    method: "GET",
    url: "https://crypto-news-live3.p.rapidapi.com/news",
    headers: {
      "x-rapidapi-host": "crypto-news-live3.p.rapidapi.com",
      "x-rapidapi-key": "635aa96644msh580d703a5339e3bp199c01jsn9d6419943f07",
    },
  };

  axios
    .request(options)
    .then(function (response) {
      console.log(response.data);
    })
    .catch(function (error) {
      console.error(error);
    });

  return <CryptoApiContext value={{ articles }}>{children}</CryptoApiContext>;
};

export default CryptoApiProvider;
