import styled from "styled-components";

export const Title = styled.h1`
  font-size: ${({ isSmall, theme: {fontSize} }) => (isSmall ? `${({ theme }) => fontSize.xxl}` : "38px")};
  color: ${({ theme }) => theme.colors.darkGrey};
`;
